mapper.select:
SELECT COUNT(id) from message
WHERE
`status`!=2
AND from_id= 1
AND to_id=#{userId}
AND conversation_id=#{topic}

SELECT COUNT(id) from message
WHERE
from_id= 1
AND to_id=#{userId}
AND `status`= 0

    AND conversation_id=#{topic}

SELECT 
FROM message
WHERE id in(
    SELECT max(id) from message
    where `status`!=2
    AND from_id= 1
    AND to_id=#{userId}
    AND conversation_id=#{topic}
)

SELECT * from message
WHERE
`status`!=2  AND from_id =1 AND to_id=#{userId}
AND conversation_id=#{topic}
ORDER BY id desc
LIMIT #{offset},#{limit}
