mapper.select:
SELECT * from message
WHERE
`status`!=2  AND from_id!=1
AND conversation_id=#{conversationId}
LIMIT #{offset},#{limit}

SELECT COUNT(id) from message
WHERE
`status`=0  AND from_id!=1
AND to_id=#{userId}

AND conversation_id=#{conversationId}

mapper.update:
update message
set status=#{status}
where `id` in

    #{id}
